mapper.select:
select
  book_id, book_no, name, author, type, status, create_date
from book


        and book_id = #{bookId}


        and book_no = #{bookNo}


        and name = #{name}


        and author = #{author}


        and type = #{type}


        and status = #{status}


        and create_date = #{createDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from book


        and book_id = #{bookId}


        and book_no = #{bookNo}


        and name = #{name}


        and author = #{author}


        and type = #{type}


        and status = #{status}


        and create_date = #{createDate}
