mapper.select:
select
  notice_id, title, content, create_time, user_id
from notice


        and notice_id = #{noticeId}


        and title = #{title}


        and content = #{content}


        and create_time = #{createTime}


        and user_id = #{userId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from notice


        and notice_id = #{noticeId}


        and title = #{title}


        and content = #{content}


        and create_time = #{createTime}


        and user_id = #{userId}
