mapper.select:
select
  user_id, username, email, password, type, status
from user


        and user_id = #{userId}


        and username = #{username}


        and email = #{email}


        and password = #{password}


        and type = #{type}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and user_id = #{userId}


        and username = #{username}


        and email = #{email}


        and password = #{password}


        and type = #{type}


        and status = #{status}
