mapper.select:
select
  user_id, username, email, password, type, status, tel
from user


        and user_id = #{userId}


        and username = #{username}


        and email = #{email}


        and password = #{password}


        and type = #{type}


        and status = #{status}


        and tel = #{tel}

select
  user_id, username, email, password, type, status, tel
from user


        and user_id = #{user.userId}


        and username = #{user.username}


        and email = #{user.email}


        and password = #{user.password}


        and type = #{user.type}


        and status = #{user.status}


        and tel = #{user.tel}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and user_id = #{userId}


        and username = #{username}


        and email = #{email}


        and password = #{password}


        and type = #{type}


        and status = #{status}


        and tel = #{tel}
